		var screenWidth = $(document).width();
		var screenHeight = $(document).height();
		var formWidth = 220;
		var formHeigth = 210;

		this.$el.css('left', (screenWidth / 2) - (formWidth / 2));
		this.$el.css('top', 150);

		this.action = options.action;

